SELECT MP.id AS id, MP.part_name AS partName, MW.work_year AS workYear, MW.work_month AS workMonth, time_format( SEC_TO_TIME( SUM( CASE WHEN ( MP.part_name NOT IN ( '生产运行开发中心', '质控中心', '商务营销开发中心', '行政服务开发中心' ) ) AND MW.`week` IN ( 6, 7 ) THEN TIME_TO_SEC( TIMEDIFF( end_time, '17:30:00' ) ) * 2 ELSE TIME_TO_SEC( TIMEDIFF( end_time, '17:30:00' ) ) END)),'%H:%I:%S') AS talOverTime FROM mf_work MW LEFT JOIN mf_user_info MUI ON MW.user_id = MUI.id LEFT JOIN mf_part MP ON MUI.part_id = MP.id WHERE MW.end_time > '17:30:00'
AND MP.id = #{query.partId}
AND MW.work_date >= #{query.workStartDate}
AND MW.work_date <= #{query.workEndDate}
GROUP BY MP.id, MP.part_name, MW.work_year, MW.work_month

SELECT overtime.workYear, overtime.workMonth, overtime.talOverTime/((MWT.work_day * MWT.work_time * 3600 * (select count(1) from mf_user_info))) * 100 as overTimeRate FROM ( SELECT MW.work_year AS workYear, MW.work_month AS workMonth, SUM( CASE WHEN (( MP.part_name NOT IN ( '生产运行开发中心', '质控中心', '商务营销开发中心', '行政服务开发中心' ) ) AND MW.`week` IN ( 6, 7 )) THEN TIME_TO_SEC( TIMEDIFF( end_time, '17:30:00' ) ) * 2 ELSE TIME_TO_SEC( TIMEDIFF( end_time, '17:30:00' ) ) END ) AS talOverTime FROM mf_work MW LEFT JOIN mf_user_info MUI ON MW.user_id = MUI.id LEFT JOIN mf_part MP ON MUI.part_id = MP.id WHERE MW.end_time > '17:30:00'
AND MW.work_date >= #{query.workStartDate}
AND MW.work_date <= #{query.workEndDate}
GROUP BY MW.work_year, MW.work_month ) overtime LEFT JOIN mf_work_time MWT ON MWT.work_year = overtime.workYear AND MWT.work_month = overtime.workMonth

SELECT overtime.workYear, overtime.workMonth, overtime.talOverTime / ( ( MWT.work_day * MWT.work_time * 3600 * ( SELECT count( A.id ) FROM mf_user_info A LEFT JOIN mf_part B ON A.part_id = B.ID WHERE B.part_name IN ( '生产运行开发中心', '质控中心', '商务营销开发中心', '行政服务开发中心' ) ) ) ) * 100 AS overTimeRate FROM ( SELECT MW.work_year AS workYear, MW.work_month AS workMonth, SUM( CASE
WHEN ( ( MP.part_name NOT IN ( '生产运行开发中心', '质控中心', '商务营销开发中心', '行政服务开发中心' ) ) AND MW.`week` IN ( 6, 7 ) ) THEN TIME_TO_SEC( TIMEDIFF( end_time, '17:30:00' ) ) * 2 ELSE TIME_TO_SEC( TIMEDIFF( end_time, '17:30:00' ) ) END ) AS talOverTime FROM mf_work MW LEFT JOIN mf_user_info MUI ON MW.user_id = MUI.id LEFT JOIN mf_part MP ON MUI.part_id = MP.id WHERE MW.end_time > '17:30:00' AND MP.part_name IN ( '生产运行开发中心', '质控中心', '商务营销开发中心', '行政服务开发中心' )
AND MW.work_date >= #{query.workStartDate}
AND MW.work_date <= #{query.workEndDate}
GROUP BY MW.work_year, MW.work_month ) overtime LEFT JOIN mf_work_time MWT ON MWT.work_year = overtime.workYear AND MWT.work_month = overtime.workMonth

SELECT overtime.workYear, overtime.workMonth, overtime.talOverTime / ( ( MWT.work_day * MWT.work_time * 3600 * ( SELECT count( 1 ) FROM mf_user_info A LEFT JOIN mf_part B ON A.part_id = B.ID WHERE B.part_name NOT IN ( '生产运行开发中心', '质控中心', '商务营销开发中心', '行政服务开发中心' ) ) ) ) * 100 AS overTimeRate FROM ( SELECT MW.work_year AS workYear, MW.work_month AS workMonth, SUM( CASE
WHEN ( ( MP.part_name NOT IN ( '生产运行开发中心', '质控中心', '商务营销开发中心', '行政服务开发中心' ) ) AND MW.`week` IN ( 6, 7 ) ) THEN TIME_TO_SEC( TIMEDIFF( end_time, '17:30:00' ) ) * 2 ELSE TIME_TO_SEC( TIMEDIFF( end_time, '17:30:00' ) ) END ) AS talOverTime FROM mf_work MW LEFT JOIN mf_user_info MUI ON MW.user_id = MUI.id LEFT JOIN mf_part MP ON MUI.part_id = MP.id WHERE MW.end_time > '17:30:00' AND MP.part_name NOT IN ( '生产运行开发中心', '质控中心', '商务营销开发中心', '行政服务开发中心' )
AND MW.work_date >= #{query.workStartDate}
AND MW.work_date <= #{query.workEndDate}
GROUP BY MW.work_year, MW.work_month ) overtime LEFT JOIN mf_work_time MWT ON MWT.work_year = overtime.workYear AND MWT.work_month = overtime.workMonth

SELECT MP.id AS id, MP.part_name AS partName, MW.work_year AS workYear, MW.work_month AS workMonth, time_format( SEC_TO_TIME( SUM( CASE WHEN ( MP.part_name NOT IN ( '生产运行开发中心', '质控中心', '商务营销开发中心', '行政服务开发中心' ) ) AND MW.`week` IN ( 6, 7 ) THEN TIME_TO_SEC( TIMEDIFF( end_time, '17:30:00' ) ) * 2 ELSE TIME_TO_SEC( TIMEDIFF( end_time, '17:30:00' ) ) END)),'%H:%I:%S') AS talOverTime FROM mf_work MW LEFT JOIN mf_user_info MUI ON MW.user_id = MUI.id LEFT JOIN mf_part MP ON MUI.part_id = MP.id WHERE MW.end_time > '17:30:00'
AND MP.id = #{query.partId}
AND (MW.work_year >= #{query.workYearStart} AND MW.work_month >= #{query.workMonthStart} ) AND (MW.work_year <= #{query.workYearEnd} AND MW.work_month <= #{query.workMonthEnd}) GROUP BY MP.id, MP.part_name, MW.work_year, MW.work_month
