SELECT MP.id AS id, MP.part_name AS partName, SUM( CASE WHEN (( MP.part_name NOT IN ('生产运行开发中心', '质控中心', '商务营销开发中心', '行政服务开发中心' )) AND MW.`week` IN ( 6, 7 )) THEN TIME_TO_SEC( TIMEDIFF( end_time, '16:30:00' ) ) * ( SELECT t.mag_value FROM mf_magnification t WHERE t.work_date_type = 2 ) WHEN (( MP.part_name NOT IN ('生产运行开发中心', '质控中心', '商务营销开发中心', '行政服务开发中心' )) AND MW.`week` IN (1,2,3,4,5)) THEN TIME_TO_SEC( TIMEDIFF( end_time, '16:30:00' ) ) * ( SELECT t.mag_value FROM mf_magnification t WHERE t.work_date_type = 1 ) WHEN (( MP.part_name IN ('生产运行开发中心', '质控中心', '商务营销开发中心', '行政服务开发中心' )) AND MW.`week` IN ( 6, 7 )) THEN TIME_TO_SEC( TIMEDIFF( end_time, '16:30:00' ) ) * ( SELECT t.mag_value FROM mf_magnification_it t WHERE t.work_date_type = 2 ) WHEN (( MP.part_name IN ('生产运行开发中心', '质控中心', '商务营销开发中心', '行政服务开发中心' )) AND MW.`week` IN (1,2,3,4,5)) THEN TIME_TO_SEC( TIMEDIFF( end_time, '16:30:00' ) ) * ( SELECT t.mag_value FROM mf_magnification_it t WHERE t.work_date_type = 1 ) ELSE TIME_TO_SEC( TIMEDIFF( end_time, '16:30:00' ) ) END) AS talOverTime FROM mf_work MW LEFT JOIN mf_user_info MUI ON MW.user_id = MUI.id LEFT JOIN mf_part MP ON MUI.part_id = MP.id WHERE MW.end_time > '17:30:00'
AND MP.id = #{query.partId}
AND MW.work_date >= #{query.workStartDate}
AND MW.work_date <= #{query.workEndDate}
GROUP BY MP.id, MP.part_name

SELECT MP.id AS id, MP.part_name AS partName, MW.work_year AS workYear, MW.work_month AS workMonth, SUM( CASE WHEN (( MP.part_name NOT IN ('生产运行开发中心', '质控中心', '商务营销开发中心', '行政服务开发中心' )) AND MW.`week` IN ( 6, 7 )) THEN TIME_TO_SEC( TIMEDIFF( end_time, '16:30:00' ) ) * ( SELECT t.mag_value FROM mf_magnification t WHERE t.work_date_type = 2 ) WHEN (( MP.part_name NOT IN ('生产运行开发中心', '质控中心', '商务营销开发中心', '行政服务开发中心' )) AND MW.`week` IN (1,2,3,4,5)) THEN TIME_TO_SEC( TIMEDIFF( end_time, '16:30:00' ) ) * ( SELECT t.mag_value FROM mf_magnification t WHERE t.work_date_type = 1 ) WHEN (( MP.part_name IN ('生产运行开发中心', '质控中心', '商务营销开发中心', '行政服务开发中心' )) AND MW.`week` IN ( 6, 7 )) THEN TIME_TO_SEC( TIMEDIFF( end_time, '16:30:00' ) ) * ( SELECT t.mag_value FROM mf_magnification_it t WHERE t.work_date_type = 2 ) WHEN (( MP.part_name IN ('生产运行开发中心', '质控中心', '商务营销开发中心', '行政服务开发中心' )) AND MW.`week` IN (1,2,3,4,5)) THEN TIME_TO_SEC( TIMEDIFF( end_time, '16:30:00' ) ) * ( SELECT t.mag_value FROM mf_magnification_it t WHERE t.work_date_type = 1 ) ELSE TIME_TO_SEC( TIMEDIFF( end_time, '16:30:00' ) ) END) AS talOverTime FROM mf_work MW LEFT JOIN mf_user_info MUI ON MW.user_id = MUI.id LEFT JOIN mf_part MP ON MUI.part_id = MP.id WHERE MW.end_time > '17:30:00'
AND MP.id = #{query.partId}
AND (MW.work_year >= #{query.workYearStart} AND MW.work_month >= #{query.workMonthStart} ) AND (MW.work_year <= #{query.workYearEnd} AND MW.work_month <= #{query.workMonthEnd}) GROUP BY MP.id, MP.part_name, MW.work_year, MW.work_month
