mapper.select:
SELECT t.pname as pname, t.pcode as pcode, t.work_year as workYear, t.work_month as workMonth, time_format(SEC_TO_TIME( t.ot ),'%H:%I:%S') AS totalOverTime FROM ( SELECT mui.pname, mui.pcode, mw.work_year, mw.work_month, SUM( TIME_TO_SEC( ( CASE WHEN mw.end_time > '17:30:00' THEN TIMEDIFF( mw.end_time, '17:30:00' ) END ) ) ) AS ot FROM mf_user_info mui LEFT JOIN mf_work mw ON mui.id = mw.user_id WHERE 1=1
AND mui.pcode = #{query.pcode}
AND mw.work_year >= #{query.workYearStart} AND mw.work_year <= #{query.workYearEnd} AND mw.work_month >= #{query.workMonthStart} AND mw.work_month <= #{query.workMonthEnd} ) t;

SELECT t.pname AS pname, t.pcode AS pcode, t.work_year AS workYear, t.work_month AS workMonth, time_format( SEC_TO_TIME( sum( t.ot ) ), '%H:%I:%S' ) AS totalOverTime FROM ( SELECT mui.pname, mui.pcode, mw.work_year, mw.work_month, SUM( TIME_TO_SEC( ( CASE WHEN mw.end_time > '17:30:00' THEN TIMEDIFF( mw.end_time, '17:30:00' ) END ) ) ) AS ot FROM mf_user_info mui LEFT JOIN mf_work mw ON mui.id = mw.user_id WHERE 1 = 1
AND mui.pcode = #{query.pcode}
AND mw.work_year >= #{query.workYearStart} AND mw.work_year <= #{query.workYearEnd} AND mw.work_month >= #{query.workMonthStart} AND mw.work_month <= #{query.workMonthEnd} AND MW.`week` IN ( 1, 2, 3, 4, 5 ) UNION all SELECT mui.pname, mui.pcode, mw.work_year, mw.work_month, ( TIME_TO_SEC( ( TIMEDIFF( mw.end_time, mw.start_time ) ) ) * (select t.mag_value from mf_magnification t where t.work_date_type = 2) ) AS ot FROM mf_user_info mui LEFT JOIN mf_work mw ON mui.id = mw.user_id WHERE 1 = 1
AND mui.pcode = #{query.pcode}
AND mw.work_year >= #{query.workYearStart} AND mw.work_year <= #{query.workYearEnd} AND mw.work_month >= #{query.workMonthStart} AND mw.work_month <= #{query.workMonthEnd} AND mw.`week` IN ( 6, 7 ) ) t;
